(53, 'woocommerce-shipping-fedex-api-outage-2023-07-16', 'warning', 'en_US', 'Scheduled FedEx API outage — July 2023', 'On July 16 there will be a full outage of the FedEx API from 04:00 to 08:00 AM UTC. Due to planned maintenance by FedEx, you\'ll be unable to provide FedEx shipping rates during this time. Follow the link below for more information and recommendations on how to minimize impact.', '{}', 'pending', 'woocommerce.com', '2023-08-25 12:59:31', NULL, 0, 'plain', '', 0, 0, 'info'),
(54, 'wcship-2023-07-hazmat-update-needed', 'update', 'en_US', 'Action required: USPS HAZMAT compliance update for WooCommerce Shipping & Tax extension', '<strong>Your store requires an update for the WooCommerce Shipping extension</strong>. Please update to the latest version of the WooCommerce Shipping &amp; Tax extension immediately to ensure compliance with new USPS HAZMAT rules currently in effect.', '{}', 'pending', 'woocommerce.com', '2023-08-25 12:59:31', NULL, 0, 'plain', '', 0, 0, 'info'),
(55, 'woocommerce-WCStripe-Aug-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe plugin', '<strong>Your shop requires an important security update for the  WooCommerce Stripe plugin</strong>. The fix for this vulnerability was released on July 31. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2023-08-25 12:59:31', NULL, 0, 'plain', '', 0, 0, 'info'),
(56, 'woocommerce-WCStripe-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooCommerce Stripe plugin', '<strong>Your store has been updated to the latest secure version of the WooCommerce Stripe plugin</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2023-08-25 12:59:31', NULL, 0, 'plain', '', 0, 0, 'info'),
(57, 'woocommerce-WooPayments-Aug-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooPayments (WooCommerce Payments) plugin', '<strong>Your shop requires an important security update for the WooPayments (WooCommerce Payments) extension</strong>. The fix for this vulnerability was released on July 31. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2023-08-25 12:59:31', NULL, 0, 'plain', '', 0, 0, 'info'),
(58, 'woocommerce-WooPayments-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooPayments (WooCommerce Payments) plugin', '<strong>Your store has been updated to the more secure version of WooPayments (WooCommerce Payments)</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2023-08-25 12:59:31', NULL, 0, 'plain', '', 0, 0, 'info'),
(59, 'avalara_q3-2023_noAvaTax', 'marketing', 'en_US', 'Automatically calculate VAT in real time', 'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.', '{}', 'pending', 'woocommerce.com', '2023-08-25 12:59:31', NULL, 0, 'plain', '', 0, 0, 'info'),
(60, 'woo-activation-survey-blockers-22AUG23', 'info', 'en_US', 'How can we help you get that first sale?', 'Your feedback is vital. Please take a minute to share your experience of setting up your new store and whether anything is preventing you from making those first few sales. Together, we can make Woo even better!', '{}', 'unactioned', 'woocommerce.com', '2023-09-03 14:22:42', NULL, 0, 'plain', '', 0, 1, 'info'),
(61, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Sambungkan ke WooCommerce.com', 'Sambungkan untuk mendapatkan pemberitahuan dan pembaruan produk penting.', '{}', 'unactioned', 'woocommerce-admin', '2023-08-25 12:59:31', NULL, 0, 'plain', '', 1, 1, 'info'),
(62, 'wc-admin-choosing-a-theme', 'marketing', 'en_US', 'Memilih tema?', 'Periksa tema yang kompatibel dengan WooCommerce dan pilih tema yang sesuai dengan kebutuhan merek dan bisnis Anda.', '{}', 'unactioned', 'woocommerce-admin', '2023-08-30 13:18:56', NULL, 0, 'plain', '', 1, 1, 'info'),
(63, 'wc-admin-launch-checklist', 'info', 'en_US', 'Siap meluncurkan toko Anda?', 'Untuk memastikan Anda tidak merasa melupakan sesuatu, kami telah menyusun daftar periksa pra-peluncuran yang penting.', '{}', 'unactioned', 'woocommerce-admin', '2023-08-30 13:18:56', NULL, 0, 'plain', '', 1, 1, 'info'),
(64, 'wc-admin-mobile-app', 'info', 'en_US', 'Instal aplikasi seluler Woo', 'Instal aplikasi seluler WooCommerce untuk mengelola pesanan, menerima pemberitahuan penjualan, dan melihat metrik kunci — di mana pun Anda berada.', '{}', 'unactioned', 'woocommerce-admin', '2023-08-30 13:18:56', NULL, 0, 'plain', '', 1, 1, 'info'),
(65, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'How to customize your product catalog', 'You want your product catalog and images to look great and align with your brand. This guide will give you all the tips you need to get your products looking great in your store.', '{}', 'unactioned', 'woocommerce-admin', '2023-09-03 14:22:39', NULL, 0, 'plain', '', 0, 1, 'info'),
(66, 'klaviyo_q3_2023', 'marketing', 'en_US', 'Build long lasting relationships with Klaviyo for WooCommerce', 'Increase customer engagement and boost conversions with powerful email and SMS automation from Klaviyo. Fully integrated with your Woo store, Klaviyo enables you to send personalized communications to your customers — ensuring that the right message is delivered at the right time for maximum impact.', '{}', 'pending', 'woocommerce.com', '2023-09-12 13:02:47', NULL, 0, 'plain', '', 0, 0, 'info'),
(67, 'wc-admin-orders-milestone', 'info', 'en_US', 'First order received', 'Congratulations on getting your first order! Now is a great time to learn how to manage your orders.', '{\"current_milestone\":1}', 'unactioned', 'woocommerce-admin', '2023-09-13 13:20:10', NULL, 0, 'plain', '', 0, 1, 'info'),
